import React from 'react';
import "./Main.css";
import { useDispatch, useSelector } from 'react-redux';

const Main = () => {

    const {sandwich , pizza} = useSelector(state => state.food);
    const {apple , banana , mango} = useSelector(state => state.fruit);

    const dispatch = useDispatch();

    return (
        <div className="parent-div">
            <div className="food-section">
                <h1>Foods :</h1>
                <div className="food-div">Sandwich : {sandwich}</div>
                <div className="food-div">Pizza : {pizza}</div>
            </div>
            <div className="buy-food-section">
                <div className="input-group">
                    <button type="button" className="btn btn-secondary border border-dark border-end-0" onClick={() => dispatch({type: "buy-sandwich"})}>Buy Sandwich</button>
                    <input type="number" className="form-control" placeholder="amount" />
                </div>
                <div className="input-group">
                    <button type="button" className="btn btn-secondary border border-dark border-end-0" onClick={() => dispatch({type: "buy-pizza"})}>Buy Pizza</button>
                    <input type="number" className="form-control" placeholder="amount" />
                </div>
                
            </div>
            <div className="fruit-section">
                <h1>Fruits :</h1>
                <div className="fruit-div">Apple : {apple}</div>
                <div className="fruit-div">Banana : {banana}</div>
                <div className="fruit-div">Mango : {mango}</div>
            </div>
            <diy className="buy-fruit-section">
                <button type="button" className="btn btn-secondary border border-dark border-end-0" onClick={() => dispatch({type: "buy-apple"})}>Buy Apple</button>
                <button type="button" className="btn btn-secondary border border-dark border-end-0" onClick={() => dispatch({type: "buy-banana"})}>Buy Banana</button>
                <button type="button" className="btn btn-secondary border border-dark border-end-0" onClick={() => dispatch({type: "buy-mango"})}>Buy Mango</button>
            </diy>
        </div>
    );
}

export default Main;
