import React from "react";
import "./MyForm.css";
import { Formik , Form , FastField , ErrorMessage } from "formik";
import * as Yup from "yup";
import swal from "sweetalert";
import ShowError from "./ShowError";


const initialValues = {
    name : "" ,
    password : "" ,
    email : ""
}

const onSubmit = (values) => {
    swal({
        title : "Info" ,
        text : `${values.name} / ${values.password} / ${values.email}` ,
        icon : "info"
    })
}

const validationSchema = Yup.object({
    name : Yup.string().required("Fill the input field!").min(4 , "The name must have at least 4 characters!") ,
    password : Yup.string().required("Fill the input field!").min(10 , "The password must have at least 10 characters!") ,
    email : Yup.string().required("Fill the input field!").email("incorrect format! Email foramt must be Like:\nemail@example.com")
})


const MyForm = () => {

    return (
        <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}>
            <Form className="myForm row w-50 mx-auto my-5 p-4 rounded-3">
                <h3 className="col-12 text-center">Title</h3>
                <div className="col-12 my-3">
                        <label className="form-label" for="name">Full name</label>
                        <FastField type="text" className="form-control mb-1" placeholder="Name" id="name" name="name" />
                        <ErrorMessage name="name" component={ShowError} />
                </div>
                <div className="col-12 my-3">
                        <label className="form-label" for="password">Password</label>
                        <FastField type="password" className="form-control mb-1" placeholder="Password" id="password" name="password" />
                        <ErrorMessage name="password" component={ShowError} />
                </div>
                <div className="col-12 my-3">
                        <label className="form-label" for="email">Email</label>
                        <FastField type="email" className="form-control mb-1" placeholder="Email" id="email" name="email" />
                        <ErrorMessage name="email" component={ShowError} />
                </div>
                <div className="mt-4 mb-2 text-center">
                        <button type="submit" className="btn btn-light btn-lg" value="">Submit</button>
                </div>
            </Form>
        </Formik>
    );
}

export default MyForm;
