import React from "react";
import "./Form.css";
import { useFormik } from "formik";
import * as Yup from "yup";
import swal from "sweetalert";

const Form = () => {

    const formik = useFormik({
        initialValues : {
            name : "ehsan" ,
            password : "1234" ,
            email : "ehsan.aryan2001@gmail.com"
        } ,
        onSubmit : (values) => {
            swal({
                title : "Info" ,
                text : `${values.name} / ${values.password} / ${values.email}` ,
                icon : "info"
            })
        } ,
        validationSchema : Yup.object({
            name : Yup.string().required("Fill the input field!") ,
            password : Yup.string().required("Fill the input field!") ,
            email : Yup.string().required("Fill the input field!").email("incorrect format! Email foramt must be Like:\nemail@example.com")
        })
    });


    return (
        <>
            <form className="myForm row w-50 mx-auto my-5 p-4 rounded-3">
                <h3 class="col-12 text-center">Title</h3>
                <div class="col-12 my-3">
                        <label class="form-label" for="name">Full name</label>
                        <input type="text" class="form-control" placeholder="Name" id="name" name="name" 
                        {...formik.getFieldProps("name")} />
                </div>
                <div class="col-12 my-3">
                        <label class="form-label" for="password">Password</label>
                        <input type="password" class="form-control" placeholder="Password" id="password" name="password" 
                        {...formik.getFieldProps("password")} />
                </div>
                <div class="col-12 my-3">
                        <label class="form-label" for="email">Email</label>
                        <input type="email" class="form-control" placeholder="Email" id="email" name="email" 
                        {...formik.getFieldProps("email")} />
                </div>
                <div class="mt-4 mb-2 text-center">
                        <button type="submit" class="btn btn-light btn-lg" value="">Submit</button>
                </div>
            </form>
        </>
    );
}

export default Form;
