import React from "react";
import "./MyForm.css";
import { Formik , Form , FastField , ErrorMessage } from "formik";
import * as Yup from "yup";
import swal from "sweetalert";
import ShowError from "./ShowError";
import { useState } from "react";
import { useEffect } from "react";


const initialValues = {
    name : "" ,
    password : "" ,
    email : "" ,
    bio : ""
}

const onSubmit = (values) => {
    swal({
        title : "Info" ,
        text : `Name: ${values.name}\nPassword: ${values.password}\nEmail: ${values.email}` ,
        icon : "info"
    })
}

const validationSchema = Yup.object({
    name : Yup.string().required("Fill the input field!").min(4 , "The name must have at least 4 characters!") ,
    password : Yup.string().required("Fill the input field!").min(10 , "The password must have at least 10 characters!") ,
    email : Yup.string().required("Fill the input field!").email("incorrect format! Email foramt must be Like:\nemail@example.com") ,
    bio : Yup.string().required("Fill the input field!").max(60 , "The biography must have a maximum of 60 characters!")
})


const MyForm = () => {
    const [savedData , setSavedData] = useState(null);

    useEffect(() => {
        if(localStorage.getItem("savedData")) {
            setSavedData(JSON.parse(localStorage.getItem("savedData")));
        }
    }, []);

    return (
        <div className="pt-1">
            <Formik
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
            // validateOnChange={false}
            validateOnBlur={false}
            validateOnMount
            >
                {formik => {
                    return(
                        <Form className="myForm row w-50 mx-auto my-5 p-2 rounded-4">
                            <h3 className="col-12 text-center">Title</h3>
                            <div className="col-12 my-3">
                                    <label className="form-label" for="name">Full name</label>
                                    <FastField type="text" className="form-control mb-1" placeholder="Name" id="name" name="name" />
                                    <ErrorMessage name="name">
                                        {error => {
                                            return (
                                                <div className="text-dark bg-danger text-center">
                                                    {error}
                                                </div>
                                            );
                                        }}
                                    </ErrorMessage>
                            </div>
                            <div className="col-12 my-3">
                                    <label className="form-label" for="password">Password</label>
                                    <FastField type="password" className="form-control mb-1" placeholder="Password" id="password" name="password" />
                                    <ErrorMessage name="password" component={ShowError} />
                            </div>
                            <div className="col-12 my-3">
                                    <label className="form-label" for="email">Email</label>
                                    <FastField type="email" className="form-control mb-1" placeholder="Email" id="email" name="email" />
                                    <ErrorMessage name="email" component={ShowError} />
                            </div>
                            <div className="col-12 my-3">
                                    <label className="form-label" for="bio">Biography</label>
                                    <FastField type="text" className="form-control mb-1" placeholder="Bio" id="bio" name="bio"
                                    component="textarea" />
                                    <ErrorMessage name="bio" component={ShowError} />
                            </div>
                            <div className="mt-4 mb-2 text-center">
                                    <button type="submit" className="btn btn-light border-dark btn-lg" 
                                    disabled={!(formik.dirty && formik.isValid) || formik.isSubmitting}>
                                        Submit
                                    </button>
                                    <button type="button" className="btn btn-warning  btn-lg" 
                                    disabled={!(formik.dirty && formik.isValid) || formik.isSubmitting}>
                                        Submit
                                    </button>
                            </div>
                        </Form>
                    )
                }}
            </Formik>
        </div>
    );
}

export default MyForm;
