import foodReducer from "./food/foodReducer";
import fruitReducer from "./fruit/fruitReducer";
import { combineReducers } from "redux";

const allReducers = combineReducers({
    food : foodReducer ,
    fruit : fruitReducer
});

export default allReducers;
