import { ErrorMessage, FastField } from 'formik';
import React from 'react'
import ShowError from './ShowError';

const FieldControl = (props) => {
    const {control , type , name , label , placeHolder} = props;

    switch (control) {
        case "input":
            return (
                <>
                    <label className="form-label" for={name}>{label}</label>
                    <FastField type={type} className="form-control mb-1" placeHolder={placeHolder} id={name} name={name} />
                    <ErrorMessage name={name} component={ShowError} />
                </>
            );
            break;

        case "textarea":
            return (
                <>
                    <label className="form-label" for={name}>{label}</label>
                    <FastField className="form-control mb-1" placeHolder={placeHolder} id={name} name={name} component={control} />
                    <ErrorMessage name={name} component={ShowError} />
                </>
            );
            break;
    
        default:
            return null;
            break;
    }
}

export default FieldControl;
