const foodState = {
    sandwich : 20 ,
    pizza : 10
}

const foodReducer = (prevState = foodState , action) => {
    switch (action.type) {
        case "buy-sandwich":
            return {...prevState , sandwich : prevState.sandwich - 1};
            break;

        case "buy-pizza":
            return {...prevState , pizza : prevState.pizza - 1};
            break;
        
        default:
            return prevState;
            break;
    }
}

export default foodReducer;
