import React from "react";
import CustomInput from "./CustomInput";
import CustomTextarea from "./CustomTextarea";

const GetField = (props) => {
    const {control} = props;

    switch (control) {
        case "input":
                return (
                    <>
                        <CustomInput {...props} />
                    </>
                )
            break;
        
        case "textarea" :
            return (
                <>
                    <CustomTextarea {...props} />
                </>
            )
            break;
    
        default:
            return null;
            break;
    }

}

export default GetField;
