import React from 'react'
import moment from 'moment-jalaali';
import { useState } from 'react';
import "./MyForm.css";
import { FastField } from 'formik';
import { useEffect } from 'react';

const CustomDatePicker = (props) => {
    const {name , placeholder , label} = props;
    
    const [day , setDay] = useState([])
    const [month , setMonth] = useState([])
    const [year , setYear] = useState([])
    const [showSelecbox , setShowSelectbox] = useState(false)

    useEffect(() => {
        const nowDate = moment();
        setDay(nowDate.jDate());
        setMonth(nowDate.jMonth() + 1);
        setYear(nowDate.jYear());
    }, []);

    const handleShowSelectbox = () => {
        setShowSelectbox(true);
    }

    
  return (
    <div className="datepicker-container">
        <label htmlFor={name}>{label} : </label>
        <span onClick={handleShowSelectbox}>
            <FastField type="text" className="custom-datepicker" name={name} id={name} placeholder={placeholder} />
        </span>

        { showSelecbox ? (
            <div className="selectboxes-parent">
                <div className="selectbox-parent">
                    <select className="date-selectbox">
                        {days.map(day => {
                            return (
                                <option key={`key_${day}`} value={`day_${day}`}>{day}</option>
                            )
                        })}
                    </select>
                </div>
            </div>
        ) : null}
    </div>
  )
}

export default CustomDatePicker;
