import React from "react";
import { useSelector , useDispatch } from "react-redux";
import "./Users.css";
import axios from "axios";


const Users = () => {

    const {loading , data , error} = useSelector(state => state.users);
    const dispatch = useDispatch();


    const getUsersHandler = () => {
        dispatch({type : "send-users-request"});

        axios.get("https://jsonplaceholder.typicode.com/users")
        .then(response => {
            dispatch({type : "get-users-response" , payload : response.data});
        })
        .catch(error => {
            dispatch({type : "get-users-error" , payload : error.message});
        });
    }


    return (
        <div className="main-div pt-5">
            <p className="text-center">
                <button type="button" className="btn btn-outline-dark btn-lg"
                onClick={getUsersHandler}>
                    Get Users
                </button>
            </p>

            {loading ? (
                <div class="spinner-border text-dark">
                    <span class="visually-hidden">Loading...</span>
                </div>
            ) : data.length > 0 ? (
                <ul className="text-center mt-5 list-unstyled">
                    {data.map(user => {
                        return <li key={user.id} className="my-2 fs-4">{user.id} - {user.username}</li>
                    })}
                </ul>
            ) : error ? (
                <h1 className="my-4 text-center">Something is wrong with API connection!</h1>
            ) : (
                <div className="fs-1 text-center mt-5">You can get the users by clicking on the button!</div>
            )}
        </div>
    );
}

export default Users;
