import React from "react";
import "./Login.css";
import { Formik , Form, FastField, ErrorMessage } from "formik";
import * as Yup from "yup";
import swal from "sweetalert";
import ShowLoginError from "./ShowLoginErrors";


const initialValues = {
    username : "" ,
    password : ""
}

const onSubmit = (values , submitProps) => {
    swal({
        title: "Done" ,
        text : `username : ${values.username} \n password : ${values.password}` ,
        icon : "success" ,
        button: "OK"
    })
    submitProps.resetForm()
}

const validationSchema = Yup.object({
    username : Yup.string().required("Fill the field!").matches(/[0-9]/ , "The username must contain at least a number!") ,
    password : Yup.string().required("Fill the field!").min(8 , "The password must be longer than 7 characters!").matches(/^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])/ , "The password must contain at least an uppercase character and a number and a symbol!")
})

const Login = () => {
    return (
        <div className="form-container">
            <Formik
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
            >
                {formik => {
                    return (
                        <Form className="loginForm w-75 mx-auto rounded-3 p-3 row">
                            <div className="col-12 text-center mb-4">
                                <i className="bi bi-person-circle large-icon"></i>
                            </div>
                            <div className="col-6 d-flex justify-content-center align-items-center text-center fs-3">
                                Welcome! <br />
                                Please enter your username & password.
                            </div>
                            <div className="col-6">
                                <div className="my-3 fs-5">
                                    <label htmlFor="username" className="form-label">Username</label>
                                    <FastField type="text" className="form-control" name="username" id="username" placeholder="Username" />
                                    <ErrorMessage name="username" component={ShowLoginError} />
                                </div>
                                <div className="my-3 fs-5">
                                    <label htmlFor="password" className="form-label">Password</label>
                                    <FastField type="text" className="form-control" name="password" id="password" placeholder="Password" />
                                    <ErrorMessage name="password" component={ShowLoginError} />
                                </div>
                                <div className="text-center mt-5 mb-3">
                                    <button type="submit" className="btn btn-success btn-lg">Submit</button>
                                </div>
                            </div>
                        </Form>
                    )
                }}
            </Formik>
        </div>
    )
}

export default Login;
