const fruitState = {
    apple : 20 ,
    banana : 10 ,
    mango : 5
}

const fruitReducer = (prevState = fruitState , action) => {
    switch (action.type) {
        case "buy-apple":
            return {...prevState , apple : prevState.apple - 1};
            break;

        case "buy-banana":
            return {...prevState , banana : prevState.banana - 1};
            break;

        case "buy-mango":
            return {...prevState , mango : prevState.mango - 1};
            break;
    
        default:
            return prevState;
            break;
    }
}

export default fruitReducer;
